const assert = require('assert');
const signer = require('./index');

const password = '012345678901234567890123456789AB';
const certificate = '';
const content = '{}';
const expectedSignature = '';

it('signatures should match', (done) => {
    signer.signContent(password, certificate, content).then(result => {
        assert.equal(result, expectedSignature);
    }).then(done, done);
});
